/* Add some basic styling for the button widget */
.button-widget {
    text-align: center;
    margin: 20px;
}

#myButton {
    padding: 10px 20px;
    font-size: 16px;
    font-weight: bold;
    text-align: center;
    text-decoration: none;
    background-color: #3498db; /* Change the color to your preference */
    color: #fff;
    border: 1px solid #2980b9; /* Change the color to your preference */
    border-radius: 5px;
    cursor: pointer;
}

#myButton:hover {
    background-color: #2980b9; /* Change the color to your preference */
}

srcDoc.js:
appsmith.onReady(() => {
  const button = document.getElementById("myButton");
  button.innerHTML = appsmith.model.name;
  button.addEventListener("click", () => {
    appsmith.triggerEvent("onButtonClick");
  });
});

